mapper.select:
SELECT COUNT(*) FROM board WHERE is_use = 1
AND category_pk = #{categoryPk}
AND title LIKE CONCAT('%', #{title}, '%')

SELECT * , m.id AS member_pk , b.id AS board_pk , f.id AS file_pk , (SELECT count(*) FROM board_comment bc WHERE bc.board_pk = b.id AND bc.is_use = 1 ) AS comment_count FROM board b JOIN member m ON m.id = b.member_pk LEFT OUTER JOIN file f ON f.table_ref_pk = m.id AND f.is_use = 1 AND f.table_code_pk = (SELECT t.id FROM table_code t WHERE t.table_name = 'member') WHERE b.is_use = 1
AND b.category_pk = #{board.categoryPk}
AND b.title LIKE CONCAT('%', #{board.title}, '%')
ORDER BY b.id DESC LIMIT #{page.offset}, #{page.size}

SELECT *
  FROM board
 WHERE member_pk = #{memberPk}
   AND is_use  = 1
 ORDER BY id DESC
 LIMIT #{page.offset}, #{page.size}

mapper.update:
UPDATE board


        category_pk = #{categoryPk},


        title       = #{title},


        content     = #{content, typeHandler=JsonTypeHandler},

        update_at   = #{updateAt}

 WHERE id           = #{id}
   AND is_use       = 1
   AND member_pk    = #{memberPk}

UPDATE board
   SET is_use  = 0
   AND update_at = #{updateAt}
 WHERE id        = #{boardPk}
   AND is_use    = 1
   AND member_pk = #{memberPk}
