mapper.select:
SELECT COUNT(*) FROM board WHERE is_use = 1
AND category_pk = #{categoryPk}
AND title LIKE CONCAT('%', #{title}, '%')

SELECT *, u.id as user_pk, b.id as board_pk FROM board b JOIN user u ON u.id = b.user_pk WHERE b.is_use = 1
AND b.category_pk = #{board.categoryPk}
AND b.title LIKE CONCAT('%', #{board.title}, '%')
ORDER BY b.id DESC LIMIT #{page.offset}, #{page.size}

mapper.update:
UPDATE board
   SET is_use  = 0
 WHERE id      = #{boardPk}
   AND is_use  = 1
   AND user_pk = #{userPk}
