mapper.select:
SELECT * , bbh.board_id AS board_pk , bbh.id AS board_block_history_pk FROM board_block_history bbh JOIN (SELECT bbh2.id FROM board_block_history bbh2 WHERE 1 = 1
AND bbh2.member_id = #{memberPk}
LIMIT #{page.offset}, #{page.size} ) bbh3 ON bbh.id = bbh3.id JOIN board b ON bbh.board_id = b.id ORDER BY bbh.id DESC

mapper.insert:
INSERT INTO board_block_history
       (board_id, member_id, status, reason, created_at)
VALUES (#{boardId}, #{memberId}, #{status}, #{reason}, #{createdAt});
