mapper.select:
SELECT *
  FROM board_favorite bf
 WHERE bf.member_pk = #{memberPk}

SELECT exists(
    SELECT *
      FROM board_favorite bf
     WHERE bf.member_pk = #{memberPk}
       AND bf.board_pk  = #{boardPk}
)

mapper.delete:
DELETE
  FROM board_favorite
 WHERE board_pk  = #{boardPk}
   AND member_pk = #{memberPk}
