mapper.select:
SELECT COUNT(*)
  FROM login_history
 WHERE member_pk = #{memberPk}

SELECT *
  FROM login_history lh
  JOIN (SELECT lh1.id
          FROM login_history lh1
         WHERE lh1.member_pk = #{member.id}
         ORDER BY lh1.id DESC
        OFFSET #{page.offset}
         LIMIT  #{page.size}
     ) lh2 ON lh2.id = lh.id;

SELECT EXISTS(SELECT *
                FROM login_history
               WHERE member_pk  = #{memberPk}
                 AND ip         = #{ip}
                 AND create_at BETWEEN NOW() - INTERVAL '1 YEAR'
                                   AND NOW()
                  OR member_pk  = #{memberPk}
                 AND device     = #{device}
                 AND create_at BETWEEN NOW() - INTERVAL '1 YEAR'
                                   AND NOW()
             )
